import axios from 'axios';
import { baseURL } from './SummaryApi.tsx';
 const Axios = axios.create({
    baseURL: baseURL,
});

Axios.interceptors.request.use(
  (config) => {
    
    const token = localStorage.getItem('tokesxn') || "eyJhbGciOiJIUzI1NiJ9..B3ThPVKNFrwmb4M3G9ld-MKVgjFQlXWJLGKtHWjsDqw"; // Or from context if you use auth provider
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export default Axios;

